import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

declare module "express-session" {
  interface SessionData {
    authenticated?: boolean;
    userId?: string;
  }
}

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
app.use(
  session({
    secret: process.env.SESSION_SECRET || "ttrave-secret-key-2025",
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: false, // Set to true in production with HTTPS
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
    },
  })
);

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertDestinationSchema, insertContentSchema, insertContactSubmissionSchema, insertNewsletterSubscriptionSchema, insertPackageSchema } from "@shared/schema";
import { z } from "zod";

const adminAuthSchema = z.object({
  username: z.string(),
  password: z.string()
});

const updateContentSchema = z.object({
  key: z.string(),
  value: z.string()
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication middleware for admin routes
  const requireAuth = (req: Request, res: Response, next: NextFunction) => {
    if (req.session?.authenticated) {
      next();
    } else {
      res.status(401).json({ message: "Authentication required" });
    }
  };

  // Admin authentication
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = adminAuthSchema.parse(req.body);

      const user = await storage.getUserByUsername(username);
      if (user && user.password === password) {
        req.session.authenticated = true;
        req.session.userId = user.id;
        res.json({ success: true, message: "Login successful" });
      } else {
        res.status(401).json({ message: "Invalid credentials" });
      }
    } catch (error) {
      res.status(400).json({ message: "Invalid request data" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy(() => {
      res.json({ success: true, message: "Logout successful" });
    });
  });

  app.get("/api/auth/check", (req, res) => {
    res.json({ authenticated: !!req.session?.authenticated });
  });

  // Public routes
  app.get("/api/destinations", async (req, res) => {
    try {
      const destinations = await storage.getDestinations();
      res.json(destinations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch destinations" });
    }
  });

  app.get("/api/destinations/:type", async (req, res) => {
    try {
      const type = req.params.type as 'domestic' | 'international';
      if (!['domestic', 'international'].includes(type)) {
        return res.status(400).json({ message: "Invalid destination type" });
      }

      const destinations = await storage.getDestinationsByType(type);
      res.json(destinations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch destinations" });
    }
  });

  app.get("/api/content", async (req, res) => {
    try {
      const content = await storage.getContent();
      const contentMap = content.reduce((acc, item) => {
        acc[item.key] = item.value;
        return acc;
      }, {} as Record<string, string>);
      res.json(contentMap);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch content" });
    }
  });

  // Contact form submission
  app.post("/api/contact", async (req, res) => {
    try {
      const submission = insertContactSubmissionSchema.parse(req.body);
      const created = await storage.createContactSubmission(submission);
      res.json({ success: true, message: "Message sent successfully", id: created.id });
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid form data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to submit contact form" });
      }
    }
  });

  // Newsletter subscription
  app.post("/api/newsletter", async (req, res) => {
    try {
      const subscription = insertNewsletterSubscriptionSchema.parse(req.body);
      await storage.createNewsletterSubscription(subscription);
      res.json({ success: true, message: "Successfully subscribed to newsletter" });
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid email address", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to subscribe to newsletter" });
      }
    }
  });

  // Protected admin routes
  app.get("/api/admin/destinations", requireAuth, async (req, res) => {
    try {
      const destinations = await storage.getDestinations();
      res.json(destinations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch destinations" });
    }
  });

  app.post("/api/admin/destinations", requireAuth, async (req, res) => {
    try {
      const destination = insertDestinationSchema.parse(req.body);
      const created = await storage.createDestination(destination);
      res.json(created);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid destination data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create destination" });
      }
    }
  });

  app.put("/api/admin/destinations/:id", requireAuth, async (req, res) => {
    try {
      const id = req.params.id;
      const updates = insertDestinationSchema.partial().parse(req.body);
      const updated = await storage.updateDestination(id, updates);

      if (updated) {
        res.json(updated);
      } else {
        res.status(404).json({ message: "Destination not found" });
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid destination data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update destination" });
      }
    }
  });

  app.delete("/api/admin/destinations/:id", requireAuth, async (req, res) => {
    try {
      const id = req.params.id;
      const deleted = await storage.deleteDestination(id);

      if (deleted) {
        res.json({ success: true, message: "Destination deleted successfully" });
      } else {
        res.status(404).json({ message: "Destination not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete destination" });
    }
  });

  app.put("/api/admin/content", requireAuth, async (req, res) => {
    try {
      const updates = z.array(updateContentSchema).parse(req.body);

      const updatedContent = await Promise.all(
        updates.map(async ({ key, value }) => {
          const existing = await storage.getContentByKey(key);
          if (existing) {
            return await storage.updateContent(key, value);
          } else {
            return await storage.setContent({ key, value });
          }
        })
      );

      res.json({ success: true, message: "Content updated successfully", content: updatedContent });
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid content data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update content" });
      }
    }
  });

  app.get("/api/admin/contact-submissions", requireAuth, async (req, res) => {
    try {
      const submissions = await storage.getContactSubmissions();
      res.json(submissions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch contact submissions" });
    }
  });

  app.put("/api/admin/contact-submissions/:id/status", requireAuth, async (req, res) => {
    try {
      const id = req.params.id;
      const { status } = z.object({ status: z.string() }).parse(req.body);

      const updated = await storage.updateContactSubmissionStatus(id, status);
      if (updated) {
        res.json(updated);
      } else {
        res.status(404).json({ message: "Submission not found" });
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid status data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update submission status" });
      }
    }
  });

  app.get("/api/admin/newsletter-subscriptions", requireAuth, async (req, res) => {
    try {
      const subscriptions = await storage.getNewsletterSubscriptions();
      res.json(subscriptions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch newsletter subscriptions" });
    }
  });

  app.get("/api/admin/stats", requireAuth, async (req, res) => {
    try {
      const contactSubmissions = await storage.getContactSubmissions();
      const newsletterSubscriptions = await storage.getNewsletterSubscriptions();

      const now = new Date();
      const thisMonth = new Date(now.getFullYear(), now.getMonth(), 1);
      const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);

      const thisMonthSubmissions = contactSubmissions.filter(s => s.createdAt && new Date(s.createdAt) >= thisMonth);
      const lastMonthSubmissions = contactSubmissions.filter(s => 
        s.createdAt && new Date(s.createdAt) >= lastMonth && new Date(s.createdAt) < thisMonth
      );

      const growthRate = lastMonthSubmissions.length > 0 
        ? ((thisMonthSubmissions.length - lastMonthSubmissions.length) / lastMonthSubmissions.length * 100)
        : 0;

      res.json({
        contactForms: contactSubmissions.length,
        newsletter: newsletterSubscriptions.length,
        thisMonth: thisMonthSubmissions.length,
        growth: Math.round(growthRate)
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  // Package routes
  app.get("/api/packages", async (req, res) => {
    try {
      const packages = await storage.getPackages();
      res.json(packages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch packages" });
    }
  });

  app.get("/api/packages/destination/:destinationId", async (req, res) => {
    try {
      const destinationId = req.params.destinationId;
      const packages = await storage.getPackagesByDestination(destinationId);
      res.json(packages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch packages" });
    }
  });

  // Admin package routes
  app.get("/api/admin/packages", requireAuth, async (req, res) => {
    try {
      const packages = await storage.getPackages();
      res.json(packages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch packages" });
    }
  });

  app.post("/api/admin/packages", requireAuth, async (req, res) => {
    try {
      const packageData = insertPackageSchema.parse(req.body);
      const created = await storage.createPackage(packageData);
      res.json(created);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid package data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create package" });
      }
    }
  });

  app.put("/api/admin/packages/:id", requireAuth, async (req, res) => {
    try {
      const id = req.params.id;
      const updates = insertPackageSchema.partial().parse(req.body);
      const updated = await storage.updatePackage(id, updates);

      if (updated) {
        res.json(updated);
      } else {
        res.status(404).json({ message: "Package not found" });
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid package data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to update package" });
      }
    }
  });

  app.delete("/api/admin/packages/:id", requireAuth, async (req, res) => {
    try {
      const id = req.params.id;
      const deleted = await storage.deletePackage(id);

      if (deleted) {
        res.json({ success: true, message: "Package deleted successfully" });
      } else {
        res.status(404).json({ message: "Package not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete package" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Destination, type InsertDestination, type Content, type InsertContent, type ContactSubmission, type InsertContactSubmission, type NewsletterSubscription, type InsertNewsletterSubscription, type Package, type InsertPackage } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Destinations
  getDestinations(): Promise<Destination[]>;
  getDestinationsByType(type: 'domestic' | 'international'): Promise<Destination[]>;
  getDestination(id: string): Promise<Destination | undefined>;
  createDestination(destination: InsertDestination): Promise<Destination>;
  updateDestination(id: string, destination: Partial<InsertDestination>): Promise<Destination | undefined>;
  deleteDestination(id: string): Promise<boolean>;

  // Content
  getContent(): Promise<Content[]>;
  getContentByKey(key: string): Promise<Content | undefined>;
  setContent(content: InsertContent): Promise<Content>;
  updateContent(key: string, value: string): Promise<Content | undefined>;

  // Contact Submissions
  getContactSubmissions(): Promise<ContactSubmission[]>;
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;
  updateContactSubmissionStatus(id: string, status: string): Promise<ContactSubmission | undefined>;

  // Newsletter Subscriptions
  getNewsletterSubscriptions(): Promise<NewsletterSubscription[]>;
  createNewsletterSubscription(subscription: InsertNewsletterSubscription): Promise<NewsletterSubscription>;

  // Packages
  getPackages(): Promise<Package[]>;
  getPackagesByDestination(destinationId: string): Promise<Package[]>;
  getPackage(id: string): Promise<Package | undefined>;
  createPackage(packageData: InsertPackage): Promise<Package>;
  updatePackage(id: string, packageData: Partial<InsertPackage>): Promise<Package | undefined>;
  deletePackage(id: string): Promise<boolean>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private destinations: Map<string, Destination>;
  private content: Map<string, Content>;
  private contactSubmissions: Map<string, ContactSubmission>;
  private newsletterSubscriptions: Map<string, NewsletterSubscription>;
  private packages: Map<string, Package>;

  constructor() {
    this.users = new Map();
    this.destinations = new Map();
    this.content = new Map();
    this.contactSubmissions = new Map();
    this.newsletterSubscriptions = new Map();
    this.packages = new Map();

    this.initializeDefaultData();
  }

  private initializeDefaultData() {
    // Create admin user
    const adminId = randomUUID();
    this.users.set(adminId, {
      id: adminId,
      username: "admin",
      password: "8709612003" // In production, this should be hashed
    });

    // Initialize default content
    const defaultContent = [
      { key: "site.name", value: "TTravel Hospitality" },
      { key: "hero.title", value: "Explore the World with TTRAVE" },
      { key: "hero.subtitle", value: "Book your next adventure with us!" },
      { key: "company.name", value: "TTravel Hospitality" },
      { key: "contact.phone", value: "+91 8100331032" },
      { key: "contact.email", value: "ttrave.travelagency@gmail.com" },
      { key: "contact.address", value: "B-12, Shop No. - 111/19, Saptaparni Market, Kalyani Central Park - ward no. 11, Nadia- 741235, West Bengal, India" },
      { key: "social.facebook", value: "#" },
      { key: "social.instagram", value: "#" },
      { key: "social.linkedin", value: "#" },
      { key: "social.twitter", value: "#" },
      { key: "inquiry.url", value: "https://forms.gle/your-inquiry-form-id" },
      { key: "inquiry.button.text", value: "Enquire Now" },
    ];

    defaultContent.forEach(item => {
      const id = randomUUID();
      this.content.set(item.key, {
        id,
        key: item.key,
        value: item.value,
        updatedAt: new Date()
      });
    });

    // Initialize domestic destinations (Indian states/UTs)
    const domesticDestinations = [
      "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh", "Goa",
      "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand", "Karnataka", "Kerala",
      "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya", "Mizoram", "Nagaland",
      "Odisha", "Punjab", "Rajasthan", "Sikkim", "Tamil Nadu", "Telangana",
      "Tripura", "Uttar Pradesh", "Uttarakhand", "West Bengal", "Andaman and Nicobar Islands",
      "Chandigarh", "Dadra and Nagar Haveli and Daman and Diu", "Delhi",
      "Jammu and Kashmir", "Ladakh", "Lakshadweep", "Puducherry"
    ];

    domesticDestinations.forEach(name => {
      const id = randomUUID();
      this.destinations.set(id, {
        id,
        name,
        type: "domestic",
        imageUrl: `https://images.unsplash.com/photo-1524492412937-b28074a5d7da?w=400&h=200&fit=crop`,
        formUrl: `https://forms.gle/placeholder-${name.toLowerCase().replace(/\s+/g, '-')}`,
        icon: "bi-geo-alt-fill",
        isActive: true,
        createdAt: new Date()
      });
    });

    // Initialize international destinations
    const internationalDestinations = [
      "France", "United Kingdom", "Italy", "Switzerland", "Japan", "Thailand",
      "Australia", "New Zealand", "Singapore", "Malaysia", "Dubai", "Turkey"
    ];

    internationalDestinations.forEach(name => {
      const id = randomUUID();
      this.destinations.set(id, {
        id,
        name,
        type: "international",
        imageUrl: `https://images.unsplash.com/photo-1436491865332-7a61a109cc05?w=400&h=200&fit=crop`,
        formUrl: `https://forms.gle/placeholder-${name.toLowerCase().replace(/\s+/g, '-')}`,
        icon: "bi-geo-alt-fill",
        isActive: true,
        createdAt: new Date()
      });
    });

    // Initialize sample packages
    this.initializeSamplePackages();
  }

  private initializeSamplePackages() {
    // Get some destinations to link packages to
    const destinations = Array.from(this.destinations.values());
    const apDestination = destinations.find(d => d.name === "Andhra Pradesh");
    const rajasthanDestination = destinations.find(d => d.name === "Rajasthan");
    const franceDestination = destinations.find(d => d.name === "France");

    if (apDestination) {
      const packageId = randomUUID();
      this.packages.set(packageId, {
        id: packageId,
        destinationId: apDestination.id,
        name: "Golden Triangle Tour",
        description: "Discover India's hidden gems with hand-picked tour packages across the country.",
        imageUrl: "https://images.unsplash.com/photo-1564507592333-c60657eea523?w=400&h=300&fit=crop",
        pricePerPerson: "₹25,000",
        duration: "6 Days / 5 Nights",
        highlights: ["Visit to Taj Mahal", "Red Fort Delhi", "Amber Fort Jaipur"],
        location: "Delhi - Agra - Jaipur",
        buyNowUrl: "https://forms.gle/golden-triangle-tour-booking",
        isFeatured: true,
        isActive: true,
        createdAt: new Date()
      });
    }

    if (rajasthanDestination) {
      const packageId = randomUUID();
      this.packages.set(packageId, {
        id: packageId,
        destinationId: rajasthanDestination.id,
        name: "Royal Rajasthan Experience",
        description: "Experience the royal heritage and culture of Rajasthan with our premium packages.",
        imageUrl: "https://images.unsplash.com/photo-1518548419970-58e3b4079ab2?w=400&h=300&fit=crop",
        pricePerPerson: "₹35,000",
        duration: "8 Days / 7 Nights",
        highlights: ["City Palace Udaipur", "Mehrangarh Fort Jodhpur", "Desert Safari Jaisalmer"],
        location: "Jaipur - Udaipur - Jodhpur - Jaisalmer",
        buyNowUrl: "https://forms.gle/royal-rajasthan-booking",
        isFeatured: false,
        isActive: true,
        createdAt: new Date()
      });
    }

    if (franceDestination) {
      const packageId = randomUUID();
      this.packages.set(packageId, {
        id: packageId,
        destinationId: franceDestination.id,
        name: "Paris & French Riviera",
        description: "Explore the romance of Paris and the glamour of the French Riviera in this premium package.",
        imageUrl: "https://images.unsplash.com/photo-1502602898536-47ad22581b52?w=400&h=300&fit=crop",
        pricePerPerson: "€2,500",
        duration: "10 Days / 9 Nights",
        highlights: ["Eiffel Tower Tour", "Louvre Museum", "Nice & Cannes", "Monaco Grand Prix Circuit"],
        location: "Paris - Nice - Cannes - Monaco",
        buyNowUrl: "https://forms.gle/paris-riviera-booking",
        isFeatured: true,
        isActive: true,
        createdAt: new Date()
      });
    }
  }

  // User methods
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Destination methods
  async getDestinations(): Promise<Destination[]> {
    return Array.from(this.destinations.values()).filter(d => d.isActive);
  }

  async getDestinationsByType(type: 'domestic' | 'international'): Promise<Destination[]> {
    return Array.from(this.destinations.values()).filter(d => d.type === type && d.isActive);
  }

  async getDestination(id: string): Promise<Destination | undefined> {
    return this.destinations.get(id);
  }

  async createDestination(insertDestination: InsertDestination): Promise<Destination> {
    const id = randomUUID();
    const destination: Destination = {
      ...insertDestination,
      id,
      icon: insertDestination.icon || "bi-geo-alt-fill",
      isActive: insertDestination.isActive ?? true,
      createdAt: new Date()
    };
    this.destinations.set(id, destination);
    return destination;
  }

  async updateDestination(id: string, updates: Partial<InsertDestination>): Promise<Destination | undefined> {
    const destination = this.destinations.get(id);
    if (!destination) return undefined;

    const updatedDestination = { ...destination, ...updates };
    this.destinations.set(id, updatedDestination);
    return updatedDestination;
  }

  async deleteDestination(id: string): Promise<boolean> {
    const destination = this.destinations.get(id);
    if (!destination) return false;

    const updatedDestination = { ...destination, isActive: false };
    this.destinations.set(id, updatedDestination);
    return true;
  }

  // Content methods
  async getContent(): Promise<Content[]> {
    return Array.from(this.content.values());
  }

  async getContentByKey(key: string): Promise<Content | undefined> {
    return this.content.get(key);
  }

  async setContent(insertContent: InsertContent): Promise<Content> {
    const id = randomUUID();
    const content: Content = {
      ...insertContent,
      id,
      updatedAt: new Date()
    };
    this.content.set(insertContent.key, content);
    return content;
  }

  async updateContent(key: string, value: string): Promise<Content | undefined> {
    const content = this.content.get(key);
    if (!content) return undefined;

    const updatedContent = { ...content, value, updatedAt: new Date() };
    this.content.set(key, updatedContent);
    return updatedContent;
  }

  // Contact submission methods
  async getContactSubmissions(): Promise<ContactSubmission[]> {
    return Array.from(this.contactSubmissions.values()).sort((a, b) => {
      const dateA = a.createdAt ? new Date(a.createdAt).getTime() : 0;
      const dateB = b.createdAt ? new Date(b.createdAt).getTime() : 0;
      return dateB - dateA;
    });
  }

  async createContactSubmission(insertSubmission: InsertContactSubmission): Promise<ContactSubmission> {
    const id = randomUUID();
    const submission: ContactSubmission = {
      ...insertSubmission,
      id,
      status: "pending",
      createdAt: new Date()
    };
    this.contactSubmissions.set(id, submission);
    return submission;
  }

  async updateContactSubmissionStatus(id: string, status: string): Promise<ContactSubmission | undefined> {
    const submission = this.contactSubmissions.get(id);
    if (!submission) return undefined;

    const updatedSubmission = { ...submission, status };
    this.contactSubmissions.set(id, updatedSubmission);
    return updatedSubmission;
  }

  // Newsletter subscription methods
  async getNewsletterSubscriptions(): Promise<NewsletterSubscription[]> {
    return Array.from(this.newsletterSubscriptions.values()).filter(s => s.isActive);
  }

  async createNewsletterSubscription(insertSubscription: InsertNewsletterSubscription): Promise<NewsletterSubscription> {
    // Check if email already exists
    const existing = Array.from(this.newsletterSubscriptions.values()).find(s => s.email === insertSubscription.email);
    if (existing) {
      if (!existing.isActive) {
        // Reactivate existing subscription
        const reactivated = { ...existing, isActive: true };
        this.newsletterSubscriptions.set(existing.id, reactivated);
        return reactivated;
      }
      return existing;
    }

    const id = randomUUID();
    const subscription: NewsletterSubscription = {
      ...insertSubscription,
      id,
      isActive: true,
      createdAt: new Date()
    };
    this.newsletterSubscriptions.set(id, subscription);
    return subscription;
  }

  // Package methods
  async getPackages(): Promise<Package[]> {
    return Array.from(this.packages.values()).filter(p => p.isActive);
  }

  async getPackagesByDestination(destinationId: string): Promise<Package[]> {
    return Array.from(this.packages.values()).filter(p => p.destinationId === destinationId && p.isActive);
  }

  async getPackage(id: string): Promise<Package | undefined> {
    return this.packages.get(id);
  }

  async createPackage(insertPackage: InsertPackage): Promise<Package> {
    const id = randomUUID();
    const packageData: Package = {
      ...insertPackage,
      id,
      isFeatured: insertPackage.isFeatured ?? false,
      isActive: insertPackage.isActive ?? true,
      createdAt: new Date()
    };
    this.packages.set(id, packageData);
    return packageData;
  }

  async updatePackage(id: string, updates: Partial<InsertPackage>): Promise<Package | undefined> {
    const packageData = this.packages.get(id);
    if (!packageData) return undefined;

    const updatedPackage = { ...packageData, ...updates };
    this.packages.set(id, updatedPackage);
    return updatedPackage;
  }

  async deletePackage(id: string): Promise<boolean> {
    const packageData = this.packages.get(id);
    if (!packageData) return false;

    const updatedPackage = { ...packageData, isActive: false };
    this.packages.set(id, updatedPackage);
    return true;
  }
}

export const storage = new MemStorage();

